#include <iostream>#include <string>#include <vector>using namespace std;class Solution {public:    string longestCommonPrefix(vector<string>& strs) {                string res;         if ( strs.size() == 0 ) return res;         for (unsigned int i = 0; i < strs[0].size(); i++ ) {            char c = strs[0].at(i);                         for (unsigned int j = 1; j < strs.size() ; j++ ) {                if ( i >= strs[j].size() || c != strs[j].at(i) ) {                    return res;                 }                // cout << i << ", " << j << ", " << res << endl;             }            res.push_back(c);         }        return res;     }};int main(void){    Solution sol;    vector<string> s;     s.push_back( "abcd" );    s.push_back( "abd" );        cout << sol.longestCommonPrefix( s ) << endl;             return 0;}    